#!/usr/bin/env sh

set -e
ACCOUNT=""
TOKEN=""
read -p "初始化本地jenkins配置, 是否使用默认配置 ? (y/n)" -n 1 -r

if [[ $REPLY =~ ^[Yy]$ ]]
  then
    echo ""
    ACCOUNT="username"
    TOKEN="password|token"
  else
    echo ""
    echo "jenkins账号:"
    # 20s超时自动断
